document.getElementsByClassName('infobox')[0].style.visibility = 'hidden'

function info() {
    document.getElementsByClassName('infobox')[0].style.visibility = 'visible'

    const nation = document.getElementById('name').value

    alert(`You have selected ${nation}!`)

    let h1 = document.getElementById('nationname')
    let cp = document.getElementById('continentbox')
    let cp2 = document.getElementById('capitalbox')
    let pp = document.getElementById('populationbox')
    let tp = document.getElementById('timebox')
    let up = document.getElementById('unbox')
    let pd = document.getElementById('popden')
    let picture = document.getElementById('pic')
    fetch(`https://restcountries.com/v3.1/name/${nation}`)
    .then((res) => res.json())
    .then((res) => {
        console.log(res[0])

        let title = res[0].name.common    
        console.log(title)

        let flag = res[0].flag
        console.log(flag)

        let continent = res[0].continents[0]
        console.log(continent)

        if (continent == 'Europe') {
            picture.src = 'data:image/jpeg;base64,'
        } else if (continent == 'North America') {
            picture.src = 'data:image/jpeg;base64,'
        } else if (continent == 'South America') {
            picture.src = 'data:image/jpeg;base64,'
        } else if (continent == 'Africa') {
            picture.src = 'data:image/jpeg;base64,'
        } else if (continent == 'Asia') {
            picture.src = 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSjft_neVp8rgM_8BEHHkJDVDj9oGN6bx8mUg&s'
        } else if (continent == 'Oceania') {
            picture.src = 'data:image/jpeg;base64,'
        }

        let capital = res[0].capital
        console.log(capital)

        let population = res[0].population
        console.log(population)

        let density = Math.round((res[0].population / res[0].area) * 100) / 100
        console.log(density)

        let time = res[0].timezones
        console.log(time.length)

        let un = res[0].unMember
        console.log(un)

        h1.innerHTML = `${title} ${flag}`
        cp.innerHTML = `Continent: ${continent}`
        cp2.innerHTML = `Capital: ${capital}`
        tp.innerHTML = `# of Time zones: ${time.length}`
        pp.innerHTML = `Population: ${population}`
        up.innerHTML = `Member of the United Nations: ${un}`
        pd.innerHTML = `Population Density: ${density}`
    })
}

function currinfo() {
    const currnation = document.getElementById('currname').value
    let testable = currnation.toLowerCase();
    let text = ""
    fetch(`https://restcountries.com/v3.1/currency/${testable}`)
    .then((res) => res.json())
    .then((res) => {

        for (i = 0; i < res.length; i++) {
            let name = res[i].name.common
            let nameflag = res[i].flag
            console.log(name)
            console.log(nameflag)
            text += `${name}` + " " + `${nameflag}` + "<hr>"
        }
        document.getElementById('listnames').innerHTML = text

        if (res.length == 1) {
            document.getElementById('counter').innerHTML = `1 Country Found:`
        } else if (res.length > 1) {
            document.getElementById('counter').innerHTML = `${res.length} Countries Found:`
        } else {
            document.getElementById('counter').innerHTML = `No Countries Found:`
        }
    })
}

