function chartinfo() {
    const ctx = document.getElementById('myChart');

    let result = document.getElementById('chartname').value

    fetch(`https://restcountries.com/v3.1/name/${result}`)
    .then((res) => res.json())
    .then((res) => {
        console.log(res[0])

        let name = res[0].name.common
        console.log(name)
        let flag = res[0].flag
        console.log(flag)
        let area = res[0].area
        console.log(area)
        let pop = res[0].population
        console.log(pop)

        new Chart(ctx, {
            type: 'bar',
            data: {
                labels: ['Area', 'Population'],
                datasets: [{
                    label: `Amount`,
                    data: [area, pop],
                    borderWidth: 5,
                }]
            },
            options: {
                scales: {
                    y: {
                        beginAtZero: true
                    }
                },
                plugins: {
                    title: {
                        display: true,
                        text: `${name} ${flag}Area & Population `
                    }
                },
                responsive: true
            }
        })
    }) 
}